/* Basically blatantly stolen from the DDL provided by slick */
create table "MEETINGS" (
"MEETING_TITLE" VARCHAR NOT NULL,
"MEETING_DATE" DATE NOT NULL,
"MEETING_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) PRIMARY KEY);

create table "SURVEY_RESULTS" (
"COUNT" INTEGER NOT NULL,
"TOTAL" INTEGER NOT NULL,
"MEETING_ID" INTEGER NOT NULL,
"SURVEY_RESULTS_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) PRIMARY KEY);

alter table "SURVEY_RESULTS" add constraint "MEETING_FK" foreign key("MEETING_ID") references "MEETINGS"("MEETING_ID") on update NO ACTION on delete NO ACTION;
